import styled from "styled-components";

export default styled.input`
    width: 100%;
    background: #FFF;
    box-shadow: 0 0 10px 5px rgba(0,0,0,0.1);
    border-radius: 30px;
    margin-bottom: 11px;
    border: none;
    padding: 14px;

    &:first-of-type {
        margin-top: 11px;
    }
`;
